mapper.select:
select  from bus_route_appointment


        and route_id = #{routeAppointment.routeId}


        and appointment_uid = #{routeAppointment.appointmentUid}


        and appointment_date = date_format(#{routeAppointment.appointmentDate}, '%Y-%m-%d')


        and depart_time = #{routeAppointment.departTime}


        and depart_id = #{routeAppointment.departId}


        and state = #{routeAppointment.state}
