mapper.select:
select  from bus_route


        and car_number like concat("%",#{route.carNumber}, "%")


        and car_type = #{route.carType}


        and capacity = #{route.capacity}


        and driver_uid = #{route.driverUid}


        and start_address like concat("%", #{route.startAddress}, "%")


        and end_address like concat("%", #{route.endAddress}, "%")


        and plan_depart_time like concat(#{route.planDepartTime}, "%")


        and state = #{route.state}


        and create_by = #{route.createBy}


        and create_date = #{route.createDate}


        and update_by = #{route.updateBy}


        and update_date = #{route.updateDate}

select id, car_number, car_type, capacity, driver_uid, start_address, end_address, plan_depart_time, state from bus_route br


        and car_number like concat("%",#{route.carNumber}, "%")


        and car_type = #{route.carType}


        and plan_depart_time >= #{route.planDepartTime}


        and driver_uid = #{route.driverUid}


        and start_address like concat("%", #{route.startAddress}, "%")


        and end_address like concat("%", #{route.endAddress}, "%")

    and not exists (select id from bus_route_appointment bra 
    where bra.appointment_uid = #{route.appointmentUid}
    and bra.route_id = br.id
    and bra.appointment_date = date_format(#{route.appointmentDate}, '%Y-%m-%d')
    and bra.state = 0
    )
    and not exists (select id from bus_route_depart brd 
    where brd.route_id = br.id
    and brd.state = 1
    and date_format(brd.depart_time, '%Y-%m-%d') = date_format(#{route.appointmentDate}, '%Y-%m-%d')
    )
